import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState(null);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const result = await axios.get('http://localhost:8000/ask', {
        params: {
          query: query,
        },
      });
      setResponse(result.data);
    } catch (error) {
      setError('Bir hata oluştu, lütfen tekrar deneyin.');
      console.error('Error:', error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>MongoDB Tabanlı Chatbot Uygulaması</h1>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={query}
            onChange={handleInputChange}
            placeholder="Bir soru sorun..."
          />
          <button type="submit">Sor</button>
        </form>
        {error && (
          <div className="error">
            <p>{error}</p>
          </div>
        )}
        {response && (
          <div className="response">
            <h3>En İyi Eşleşme:</h3>
            <p>Doküman ID: {response.embedding_id}</p>
            <p>Benzerlik: {response.similarity}</p>
            <p>Metin: {response.text}</p>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
