import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState({});

  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const result = await axios.post('http://localhost:8000/ask', {
        query: query,
      });
      setResponse(result.data);
    } catch (error) {
      console.error('Error:', error);
      setResponse({ error: 'Bir hata oluştu. Lütfen tekrar deneyin.' });
    }
  };

  return (
    <div className="App">
      <h1>MongoDB Tabanlı Chatbot Uygulaması</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={query}
          onChange={handleInputChange}
          placeholder="Bir soru yazın..."
        />
        <button type="submit">Sor</button>
      </form>
      {response && (
        <div className="response">
          <h3>En İyi Eşleşme:</h3>
          <p>Doküman ID: {response.embedding_id}</p>
          <p>Benzerlik: {response.similarity}</p>
          <p>Metin: {response.text}</p>
        </div>
      )}
    </div>
  );
}

export default App;
